import './css/App.css';
import {useState, useEffect} from 'react';
import { BsTrash, BsBookmarkCheck, BsBookmarkCheckFill } from 'react-icons/bs';

const API = 'https://localhost:5000'


function App() {
  const [title, setTitle] = useState("");
  const [time, setTime] = useState("");
  const [todos, setTodos] = useState([]);
  const [loading, setLoading] = useState(false);


  const HandlerSubmit = (e) => {
    e.preventDefault();
    console.log("Submit");
  }

  return (
    <div className="App">
      <div className="todo-header">
        <h1>React Todo</h1>
      </div>

      <div className="todo-main">
        <div className='form-todo'>
          <h2>Insira a sua proxima tarefa:</h2>
          <form onSubmit={HandlerSubmit}>
            <input type="text" placeholder="Titulo da tarefa" name="title" value={title} onChange={(e) => setTitle(e.target.value)} />
            <input type="submit" value="submit" name="tarefa"/>
          </form>
        </div>

        <div className="list-todo">
          <h2>Listas de Tarefas:</h2>
          {todos.length === 0 && <p>Nao possui tarefas!</p>}
        </div>
      </div>
    </div>
  );
}

export default App;
